INSERT INTO fooditem VALUES ( 000003, 'Classic Nachos', 'Hand-cut tortilla chips layered with cheddar and pizza mozzarella, topped with black olives, green peppers, tomatoes and jalapeño peppers. Served with salsa and sour cream on the side.Due to the nature of this item, we recommend it be enjoyed in the restaurant and do not offer it for Take-out or Delivery.', TRUE, NULL, 'http://i.imgur.com/tIUUdLB.png', 10.00, 0.00); 
INSERT INTO fooditem VALUES ( 000004, 'Fried Wings', 'Fried until golden and crispy."', TRUE, NULL, 'http://i.imgur.com/Zckbqza.png', 10.00, 0.00); 
INSERT INTO fooditem VALUES ( 000005, 'Garlic Toast', 'Fresh from the oven, brushed with butter and garlic.', TRUE, NULL, 'http://i.imgur.com/s4YvH87.png', 10.00, 0.00); 
INSERT INTO fooditem VALUES ( 000006, 'Salt and Pepper Dry Ribs', 'Tender pork ribs fried and tossed in a salt & pepper dry rub and finished with chopped red peppers.', TRUE, NULL, 'http://i.imgur.com/27bgJWH.png', 10.00, 0.00); 
INSERT INTO fooditem VALUES ( 000007, 'Three Cheese Bread', 'Loaded with bubbling cheddar, pizza mozzarella and Parmesan with a hint of garlic.', TRUE, NULL, 'http://i.imgur.com/3we7dRQ.jpg', 10.00, 0.00); 
