INSERT INTO menu VALUES ( 000002, TRUE, 'Entrees' );
INSERT INTO fooditem VALUES ( 000008, 'Beef Dip', 'It doesn’t get any better than our reimagined beef dip. Thinly sliced steak piled high on a toasted garlic-brushed bun with melted pizza mozzarella, braised onions, roasted red peppers and crispy onion straws. Served with horseradish mayo and jus for dipping.', TRUE, NULL, 'http://i.imgur.com/slqaMuK.png', 10.00, 0.00); 
INSERT INTO fooditem VALUES ( 000009, 'Prime Rib Burger', '100% Canadian half-pound prime rib beef burger, fresh lettuce, tomato, red onion and pickles. Plus, our secret ingredient - a zesty sauce that simply defies description. Recommended for burger connoisseurs.', TRUE, NULL, 'http://i.imgur.com/99hU5nq.png', 10.00, 0.00); 
INSERT INTO fooditem VALUES ( 000010, 'Chicken Quesadilla', 'A tortilla stuffed with tender chicken, sun-dried tomatoes, cheddar, pizza mozzarella, sun-dried tomato pesto and tangy chipotle sauce, then brushed with garlic spread and Parmesan and oven baked to golden perfection. Served with sour cream and salsa for dipping.', TRUE, NULL, 'http://i.imgur.com/ww9owCc.png', 10.00, 0.00); 
INSERT INTO fooditem VALUES ( 000011, 'Chicken Parmesan', 'A tender chicken breast, breaded and fried until golden brown, topped with our signature marinara sauce and pizza mozzarella, then baked to perfection. Served with garlic toast, seasonal vegetables and your choice of side dish.', TRUE, NULL, 'http://i.imgur.com/AIQ5AMR.png', 10.00, 0.00); 
INSERT INTO fooditem VALUES ( 000012, 'Ribs and Wings', 'Succulent, oh-so-slowly roasted pork back ribs baked in your choice of our signature BBQ, honey garlic, Jack Daniel’s® BBQ or whisky BBQ sauce, paired with Bostons famous wings prepared just the way you like them. Served with garlic toast, seasonal vegetables and your choice of side dish.', TRUE, NULL, 'http://i.imgur.com/oQc38mn.png', 10.00, 0.00); 
INSERT INTO fooditem VALUES ( 000013, 'Steak and Veggies', 'A 7-ounce New York strip loin steak, aged a minimum of 28 days to the peak of tenderness and flavour, charbroiled the way you like it. Served with seasonal vegetables and garden greens with low-fat poppy seed dressing. Sodium: 333 mg, Protein: 44 g, Calories: 446, Saturated fat: 7.8 g, Total fat: 22.9 g. Due to the nature of this item, we recommend it be enjoyed in the restaurant and do not offer it for Take-out or Delivery.', TRUE, NULL, 'http://i.imgur.com/dJGe3TQ.png', 10.00, 0.00); 
INSERT INTO fooditem VALUES ( 000014, 'Crispy Chicken Pecan Salad', 'Breaded chicken breast filet, toasted pecans, pizza mozzarella, cheddar, smoky bacon, chopped egg, julienne carrots and diced tomato over fresh garden greens tossed in ranch dressing.', TRUE, NULL, 'http://i.imgur.com/N9UbSl3.jpg', 10.00, 0.00); 
INSERT INTO fooditem VALUES ( 000015, 'Garden Salad', 'A blend of garden and artisan greens served with our house balsamic vinaigrette. Starter size only.', TRUE, NULL, 'http://i.imgur.com/zzTLX3K.png', 10.00, 0.00); 
INSERT INTO fooditem VALUES ( 000016, 'Mediterranean Salad', 'Our twist on a favourite. Chunks of cucumbers, red onions, red peppers and tomatoes tossed in a Mediterranean vinaigrette with feta and kalamata olives on a bed of garden greens.', TRUE, NULL, 'http://i.imgur.com/57z7g6I.png', 10.00, 0.00); 
INSERT INTO fooditem VALUES ( 000017, 'Caesar Salad', 'Romaine lettuce tossed with our signature creamy garlic dressing and crunchy croutons, sprinkled with freshly grated Parmesan.', TRUE, NULL, 'http://i.imgur.com/gaZKa8C.png', 10.00, 0.00); 
INSERT INTO fooditem VALUES ( 000018, 'Jambalaya Fettucini', 'Fettuccini tossed in our signature marinara sauce with tender chicken breast, shrimp, spicy Italian sausage, tomatoes, green peppers and topped with green onions.', TRUE, NULL, 'http://i.imgur.com/LJohYcU.png', 10.00, 0.00); 
INSERT INTO fooditem VALUES ( 000019, 'Lasagna', 'Our beefy Bolognese sauce layered over tender noodles, topped with pizza mozzarella, then baked to perfection and finished with fresh Parmesan.', TRUE, NULL, 'http://i.imgur.com/UjvsDSS.png', 10.00, 0.00); 
INSERT INTO fooditem VALUES ( 000020, 'Chicken and Mushroom Fettuccini', 'Tender sautéed chicken breast and fresh mushrooms tossed in our signature Marsala cream sauce with fettuccini, tomatoes and green onion.', TRUE, NULL, 'http://i.imgur.com/6STbupK.png', 10.00, 0.00); 
INSERT INTO fooditem VALUES ( 000021, 'Deluxe Pizza', 'Signature pizza sauce, pizza mozzarella, pepperoni, smoked ham, green pepper and fresh mushrooms.', TRUE, NULL, 'http://i.imgur.com/yTq4FE9.png', 10.00, 0.00); 
INSERT INTO fooditem VALUES ( 000022, 'Pepperoni Pizza', 'Signature pizza sauce, pizza mozzarella and loads of pepperoni.', TRUE, NULL, 'http://i.imgur.com/cO8saJT.png', 10.00, 0.00); 
INSERT INTO fooditem VALUES ( 000023, 'Vegetarian Pizza', 'Signature pizza sauce, pizza mozzarella, fresh mushrooms, green peppers and onions, topped with fresh tomato slices. For a lighter option try it as a small multrigrain thin crust.', TRUE, NULL, 'http://i.imgur.com/GZowsYY.png', 10.00, 0.00); 
