INSERT INTO fooditem VALUES ( 000024, 'Apple Crisp', 'Just like mom used to make it. Slices of sweet granny smith apples in a butter sauce, baked to a crisp with a delicious brown sugar and oat crust. Topped with caramel sauce and served with a scoop of vanilla bean ice cream.Due to the nature of this item, we recommend it be enjoyed in the restaurant and do not offer it for Take-out or Delivery.', TRUE, NULL, 'http://i.imgur.com/6dwDy5X.png', 10.00, 0.00); 
INSERT INTO fooditem VALUES ( 000025, 'Brownie', 'A warm chocolate brownie topped with two scoops of vanilla bean ice cream, caramel and chocolate sauce.Due to the nature of this item, we recommend it be enjoyed in the restaurant and do not offer it for Take-out or Delivery.', TRUE, NULL, 'http://i.imgur.com/BfxhW3S.png', 10.00, 0.00); 
INSERT INTO fooditem VALUES ( 000026, 'Chocolate Cake', 'Ultimate decadence. Creamy chocolate mousse with chunks of cheesecake, caramel, toffee, pecans and almonds on a chocolate crust.', TRUE, NULL, 'http://i.imgur.com/crQ4BM4.png', 10.00, 0.00); 
INSERT INTO fooditem VALUES ( 000027, 'New York Cheesecake', 'A cheesecake lover’s delight. Traditional style creamy cheesecake piled high on a graham cracker crust.', TRUE, NULL, 'http://i.imgur.com/eXkQcSn.png', 10.00, 0.00); 
